<!DOCTYPE HTML><?php get_header();?><script source="text/javascript">var $j = jQuery.noConflict();$j(document).ready(function(){ function reloadLikes(who) {	var text = $j("#" + who).text();	var patt= /(\d)+/;		var num = patt.exec(text);	num[0]++;	text = text.replace(patt,num[0]);	if(num[0] == 1) {		text = text.replace('people like','person likes');	} else if(num[0] == 2) {		text = text.replace('person likes','people like');	} //elseif	$j("#" + who).text(text);} //reloadLikes$j(".likeThis").click(function() {	var classes = $j(this).attr("class");	classes = classes.split(" ");		if(classes[1] == "done") {		return false;	}	var classes = $j(this).addClass("done");	var id = $j(this).attr("id");	id = id.split("like-");	$j.ajax({	  type: "POST",	  url: "index.php",	  data: "likepost=" + id[1],	  success: reloadLikes("like-" + id[1])	}); 		return false;});});</script>
